    private void inputNumPhoneKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_inputNumPhoneKeyPressed
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        if (Character.isLetter(c)) {
            inputNumPhone.setEditable(false);
        } else {
            inputNumPhone.setEditable(true);
        }
    }//GEN-LAST:event_inputNumPhoneKeyPressed

